import { useState, useEffect } from 'react'
import Input from './Input'
import Button from './Button'

const RegistrationPage = () => {
  const registrationInit = {
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  }
  const errorMsg = {
    username: '',
    email: '',
    confirmPassword: ''
  }
  const [registrationObj, setRegistrationObj] = useState(registrationInit)
  const [errorMsgObj, setErrorMsgsObj] = useState(errorMsg)
  const [currentTarget, setCurrentTarget] = useState({id:''})

  useEffect(() => {
    if (currentTarget && currentTarget.id) {
      (currentTarget.id === 'password' || currentTarget.id === 'confirmPassword') &&
      (registrationObj.password.length > 0 && registrationObj.confirmPassword.length > 0) &&
      checkPasswords()
    }
  }, [currentTarget, registrationObj])
  useEffect(() => {checkValidity()}, [errorMsgObj])
  useEffect(() => { console.log(registrationObj) }, [registrationObj])
  useEffect(() => { console.log('err', errorMsg) }, [errorMsg])

  const inputHandler = (e) => {
    e.preventDefault()
    setRegistrationObj({
      ...registrationObj,
      ...{ [e.target.id]: e.target.value }
    })
    setCurrentTarget(e.target)
    setMessages(e.target)
  }

  const checkValidity = () => {
    const arr = Object.entries(errorMsgObj)
    arr.forEach(entry => {
      const element = document.querySelectorAll(`#${entry[0]}`)
      element.forEach(item => {
        if (entry[1].length > 0) {
          item.classList.add('error')
          item.classList.remove('success')
        } else {
          item.classList.remove('error')
          item.classList.add('success')
        }
      })
    })

  }

  const setMessages = (target) => {
    let pattern
    let err = ''
    switch(true) {
      case target.id === 'username':
        pattern = /^.{4,10}$/
        !pattern.test(target.value) &&
          (err+= 'Username must be between 4-10 characters in length')
        break;
      case target.id === 'email':
        pattern = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
        !pattern.test(target.value) &&
          (err+= 'Incorrect email format')
        break;
      case target.id === 'password' || target.id === 'confirmPassword':
        const pMatch = registrationObj.password === registrationObj.confirmPassword
        const pLengthCorrect = registrationObj.password.length >= 8 && registrationObj.password.length <=16
        !pLengthCorrect && (err+='Must be 8-16 characters in length. ')
        !pMatch && (err+='Passwords must match. ')
      default:
        break;
    }
    setErrorMsgsObj({...errorMsgObj, ...{ [target.id]: `${err}`}})
  }

  const checkPasswords = () => {
    const target = document.querySelectorAll('[autoComplete=new-password]')
    const pMatch = registrationObj.password === registrationObj.confirmPassword
    const pLengthCorrect = registrationObj.password.length >= 8 && registrationObj.password.length <=16

    if (pMatch && pLengthCorrect) {
      target.forEach(element => {
        element.classList.remove('error')
        element.classList.add('success')
      })
      setErrorMsgsObj({...errorMsgObj, ...{ password: '', confirmPassword: ''}})
    } else {
      let err='';
      target.forEach(element => {
        element.classList.add('error')
        element.classList.remove('success')
      })
      !pLengthCorrect && (err+='Must be 8-16 characters in length. ')
      !pMatch && (err+='Passwords must match. ')
      setErrorMsgsObj({...errorMsgObj, ...{ password: `${err}` }})
    }
  }

  return (
    <>
    <form>
      <legend>Register Account</legend>
      <Input
        id="username"
        label="Username"
        min='4'
        max='10'
        value={registrationObj.username}
        error={errorMsgObj.username}
        onChange={inputHandler}
      />
      <Input
        id="email"
        label="Email"
        value={registrationObj.email}
        error={errorMsgObj.email}
        onChange={inputHandler}
      />
      <Input
        id="password"
        label="Password"
        type="password"
        min='8'
        max='16'
        autoComplete="new-password"
        value={registrationObj.password}
        onChange={inputHandler}
      />
      <Input
        id="confirmPassword"
        label="Confirm Password"
        type="password"
        min='8'
        max='16'
        autoComplete="new-password"
        value={registrationObj.confirmPassword}
        error={errorMsgObj.confirmPassword}
        onChange={inputHandler}
      />
      <Button text="Submit" />
    </form>
  </>
  )
}

export default RegistrationPage
