import {
  createAsyncThunk,
  createSlice
} from '@reduxjs/toolkit'
import { postNewUser } from '../utils/asyncActions'

// TYPESCRIPT BUILDER API EXAMPLE
// createReducer(0, builder =>
//   builder.addCase(increment, (state, action) => {
//     // action is inferred correctly here
//   })
// )

const registerUser = createAsyncThunk('users/createUser', async (obj) => {
  await postNewUser(obj)
})

const usersSlice = createSlice({
  name: 'users',
  initialState: {
    loading: 'idle',
    usersArray: []
  },
  reducers: {
    usersLoading(state, action) {
      if (state.loading === 'idle') {
        state.loading = 'pending'
      }
    },
    usersReceived(state, action) {
      if (state.loading === 'pending') {
        state.loading = 'idle'
        state.usersArray = action.payload
      }
    },
    logUsers(state) {
      console.log('Log Users:', state)
      return state
    }
  },
  // extraReducers: {
  //   [registerUser.fulfilled]: (state, action) => {

  //   }
  // }
})

const { actions, reducer } = usersSlice
export const { logUsers } = actions
export { registerUser }
export default reducer
