import { useState, useEffect } from 'react'
import Input from './Input'
import Button from './Button'

const RegistrationPage = () => {
  const registrationInit = {
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  }
  const errorMsg = {
    username: '',
    email: '',
    password: ''
  }
  const [registrationObj, setRegistrationObj] = useState(registrationInit)
  const [errorMsgObj, setErrorMsgsObj] = useState(errorMsg)
  const [currentTarget, setCurrentTarget] = useState({id:''})

  useEffect(() => {console.log(registrationObj)}, [registrationObj])
  useEffect(() => {
    if (currentTarget && currentTarget.id) {
      (currentTarget.id === 'password' || currentTarget.id === 'confirmPassword') &&
      (registrationObj.password.length > 0 && registrationObj.confirmPassword.length > 0) &&
      checkPasswords()
    }
  }, [currentTarget, registrationObj])
  useEffect(() => {console.log(errorMsgObj)}, [errorMsgObj])

  const inputHandler = (e) => {
    e.preventDefault()
    setRegistrationObj({
      ...registrationObj,
      ...{ [e.target.id]: e.target.value }
    })
    setCurrentTarget(e.target)
    switch(true) {
      case e.target.id === 'username':
        usernameValidation(e.target, e.target.value)
        break;
      case e.target.id === 'email':
        emailValidation(e.target, e.target.value)
        break;
      default:
        break;
    }
  }

  const isValid = (element, input, pattern) => {
    let valid;
    typeof pattern === 'boolean'
    ? valid = pattern
    : valid = pattern.test(input)

    if (valid) {
      element.classList.remove('error')
      element.classList.add('success')
      return true
    } else {
      element.classList.add('error')
      element.classList.remove('success')
      return false
    }
  }

  const usernameValidation = (element, input) => {
    const pattern = /^.{4,10}$/
    let err = ''
    isValid(element, input, pattern)
      ? (err='')
      : (err+= 'Username must be between 4-10 characters in length')
    setErrorMsgsObj({...errorMsgObj, ...{ username: `${err}`}})
  }

  const emailValidation = (element, input) => {
    let pattern = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    let err = ''
    isValid(element, input, pattern)
      ? (err='')
      : (err+= 'Incorrect email format')
    setErrorMsgsObj({...errorMsgObj, ...{ email: `${err}`}})
  }

  const checkPasswords = () => {
    const target = document.querySelector('[autoComplete=new-password]')
    if (registrationObj.password === registrationObj.confirmPassword) {
      target.classList.remove('error')
      target.classList.add('success')
      setErrorMsgsObj({...errorMsgObj, ...{ password: '', confirmPassword: ''}})
    } else {
      target.classList.add('error')
      target.classList.remove('success')
      setErrorMsgsObj({...errorMsgObj, ...{ password: `Passwords do not match` }})
    }
  }

  return (
    <>
    <form>
      <legend>Register Account</legend>
      <Input
        id="username"
        label="Username"
        min='4'
        max='10'
        value={registrationObj.username}
        error={errorMsgObj.username}
        onChange={inputHandler}
      />
      <Input
        id="email"
        label="Email"
        value={registrationObj.email}
        error={errorMsgObj.email}
        onChange={inputHandler}
      />
      <Input
        id="password"
        label="Password"
        type="password"
        autoComplete="new-password"
        value={registrationObj.password}
        error={errorMsgObj.password}
        onChange={inputHandler}
      />
      <Input
        id="confirmPassword"
        label="Confirm Password"
        type="password"
        autoComplete="new-password"
        value={registrationObj.confirmPassword}
        error={errorMsgObj.password}
        onChange={inputHandler}
      />
      <Button text="Submit" />
    </form>
  </>
  )
}

export default RegistrationPage
