import {
  createAsyncThunk,
  createSlice
} from '@reduxjs/toolkit'
import { postNewUser } from '../utils/asyncActions'

// TYPESCRIPT BUILDER API EXAMPLE
// createReducer(0, builder =>
//   builder.addCase(increment, (state, action) => {
//     // action is inferred correctly here
//   })
// )

export const createUserThunk = createAsyncThunk('users/createUser', async (obj) => {
  return await postNewUser(obj)
})

const usersSlice = createSlice({
  name: 'users',
  initialState: {
    loading: 'idle',
    usersArray: []
  },
  reducers: {
    usersLoading(state, action) {
      if (state.loading === 'idle') {
        state.loading = 'pending'
      }
    },
    usersReceived(state, action) {
      if (state.loading === 'pending') {
        state.loading = 'idle'
        state.usersArray = action.payload
      }
    },
    logUsers(state) {
      console.log('Log Users:', state)
      return state
    },
    registerUser(state, action) {
      state.usersArray = [...state.usersArray, action.payload ]
      // return {
      //   ...state,
      //   usersArray: [...state.usersArray, action.payload ]
      // }
    }
  },
  extraReducers: {
    // [fetchUsers.fulfilled]: (state, action) => {
    //   // Add user to the state array
    //   state.users.push(action.payload)
    // }
  }
})

const { actions, reducer } = usersSlice
export const { logUsers, registerUser } = actions
export default reducer
