import { useState, useEffect } from 'react'
import Input from './Input'
import Button from './Button'
const LoginPage = () => {
  const loginInit = {
    username: '',
    password: ''
  }
  const [loginObj, setLoginObj] = useState(loginInit)

  useEffect(() => {
    console.log(loginObj)
  }, [loginObj])

  const inputHandler = (e) => {
    e.preventDefault()
    setLoginObj({...loginObj, ...{ [e.target.id]: e.target.value }})
  }
  return (
    <form>
      <legend>Login JustSwap.it</legend>
      <Input id="username" label="Username" value={loginObj.username} onChange={inputHandler} />
      <Input
        id="password"
        label="Password"
        type="password"
        value={loginObj.password}
        onChange={inputHandler}
      />
      <Button text="Login" />
    </form>
  )
}

export default LoginPage
