import {
  createAsyncThunk,
  createSlice,
  createAction
} from '@reduxjs/toolkit'
import { getUserAccess, postNewUser } from '../utils/asyncActions'

// TYPESCRIPT BUILDER API EXAMPLE
// createReducer(0, builder =>
//   builder.addCase(increment, (state, action) => {
//     // action is inferred correctly here
//   })
// )

export type RegistrationObj = {
  username: string,
  email: string,
  password: string
}

export type LoginObj = {
  user: string,
  pass: string
}


const registerUser = createAsyncThunk('users/createUser', async (obj: RegistrationObj) => {
  return await postNewUser(obj)
})

const loginUser = createAsyncThunk('users/createUser', async (obj: LoginObj) => {
  return await getUserAccess(obj)
})

export const usersLoading = createAction('users/loading')
export const usersReceived = createAction('users/received')
export const logUsers = createAction('users/log')

const usersSlice = createSlice({
  name: 'users',
  initialState: {
    loading: 'idle',
    currentUser: {},
    usersArray: []
  },
  reducers: {
    [usersLoading.type] : (state, action) => {
      if (state.loading === 'idle') {
        state.loading = 'pending'
      }
    },
    [usersReceived.type] : (state, action) => {
      if (state.loading === 'pending') {
        state.loading = 'idle'
        state.usersArray = action.payload
      }
    },
    [logUsers.type] : (state, action) => {
      console.log('Log Users:', state.usersArray)
      return state
    }
  },
  // extraReducers: {
  //   [registerUser.fulfilled]: (state, action) => {

  //   }
  // }
})

const { actions, reducer } = usersSlice
export { registerUser, loginUser }
export default reducer
