import styled from 'styled-components'

const StyledInput = styled.main`
  .input-container {
    position: relative;
  }
  input.error {
    border: 1px solid red;
  }
  .success .input-container::after {
    display: inline-block;
    content: " ðŸ¦„";
    background: green;
    position: relative;
    top: 0;
    right: 0;
    height: 10px;
    width: 10px;
    color: green;
  }
`

function Input({
  id,
  label,
  onChange,
  value,
  type = 'text',
  required = false,
  error = '',
  autoComplete = '',
  max = '',
  min = '',
  pattern=''
}) {
  return (
    <StyledInput>
      <label htmlFor={id}>{label}</label>
      <div className="input-container">
        <input
          type={type}
          id={id}
          name={id}
          value={value}
          onChange={onChange}
          min={min}
          max={max}
          pattern={pattern}
          autoComplete={autoComplete}
          required={required}
        />
      </div>
      {
        error.length > 0 &&
        <span>{error}</span>
      }
    </StyledInput>
  )
}

export default Input
