function Input({
  id,
  label,
  onChange,
  value,
  type = 'text',
  required = false,
  error = undefined,
  autoComplete = '',
  maxlength = '',
  minlength = ''
}) {
  return (
    <>
      <label htmlFor={id}>{label}</label>
      <input
        type={type}
        id={id}
        name={id}
        value={value}
        onChange={onChange}
        autoComplete={autoComplete}
        required={required}
      />
      {
        error.length > 0 &&
        <span>{error}</span>
      }
    </>
  )
}

export default Input
