function Input({
  id,
  label,
  onChange,
  value,
  type = 'text',
  required = false,
  error = '',
  autoComplete = '',
  max = '',
  min = '',
  pattern=''
}) {
  return (
    <>
      <label htmlFor={id}>{label}</label>
      <input
        type={type}
        id={id}
        name={id}
        value={value}
        onChange={onChange}
        min={min}
        max={max}
        pattern={pattern}
        autoComplete={autoComplete}
        required={required}
      />
      {
        error.length > 0 &&
        <span>{error}</span>
      }
    </>
  )
}

export default Input
