import { useState, useEffect } from 'react'
import Input from './Input'
import Button from './Button'

const RegistrationPage = () => {
  const registrationInit = {
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  }
  const errorMsg = {
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  }
  const [registrationObj, setRegistrationObj] = useState(registrationInit)
  const [errorMsgObj, setErrorMsgsObj] = useState(errorMsg)

  useEffect(() => {
    console.log(registrationObj)
  }, [registrationObj])

  const inputHandler = (e) => {
    e.preventDefault()
    setRegistrationObj({
      ...registrationObj,
      ...{ [e.target.id]: e.target.value }
    })
    switch(true) {
      case e.target.id === 'username':
        usernameValidation()
        break;
      default:
        break;
    }
  }

  const usernameValidation = () => {
    if (registrationObj.username.length > 10 ) {
      console.log('its over')
      setErrorMsgsObj({...errorMsgObj, ...{ username: 'Over 25 chars'}})
    }
  }

  return (
    <>
    <form>
      <legend>Register Account</legend>
      <Input
        id="username"
        label="Username"
        value={registrationObj.username}
        error={errorMsgObj.username}
        onChange={inputHandler}
      />
      <Input
        id="email"
        label="Email"
        value={registrationObj.email}
        onChange={inputHandler}
      />
      <Input
        id="password"
        label="Password"
        type="password"
        autoComplete="new-password"
        value={registrationObj.password}
        onChange={inputHandler}
      />
      <Input
        id="confirmPassword"
        label="Confirm Password"
        type="password"
        value={registrationObj.confirmPassword}
        onChange={inputHandler}
      />
      <Button text="Submit" />
    </form>
  </>
  )
}

export default RegistrationPage
