import styled from 'styled-components'
import LoginPage from '../components/LoginPage'
import RegistrationPage from '../components/RegistrationPage'

const App = styled.main`
  font-size: ${({ theme }) => theme.fontSize.base};

  form {
    margin: 0 auto;
    max-width: 30rem;
    display: flex;
    flex-flow: column nowrap;
  }
  legend {
    margin: 1rem auto;
    font-size: 1.2rem;
  }
  label + input {
    margin-bottom: 1rem;
  }
  input {
    position: relative;
  }
  input.error {
    border: 1px solid red;
  }
  input.success::after {
    content: '';
    background: green;
    position: relative;
    top: 0;
    right: 0;
    height: 1rem;
    width: 1rem;
    display: inline-block;
    color: green;
  }
`

function HomePage() {
  return (
    <App>
      <LoginPage />
      <RegistrationPage />
    </App>
  )
}

export default HomePage
