function Input({
  id,
  label,
  onChange,
  value,
  type = 'text',
  required = false
}) {
  return (
    <>
      <label htmlFor={id}>{label}</label>
      <input
        type={type}
        id={id}
        name={id}
        value={value}
        onChange={onChange}
        required={required}
      />
    </>
  )
}

export default Input
